import React from 'react'
import { useState } from 'react';
import { NavLink } from 'react-router-dom';

export default function Login() {

    const [user, setUser] = useState({
        email : '',
        password : ''
    });

    // Handle Input
    const handleChange = (event) =>{
        let name =  event.target.name;
        let value = event.target.value;

        setUser({...user, [name]:value})
    }

    //Handle Login
    const handleSubmit = async (event)=>{
        event.preventDefault();
        const {email, password} = user;
        try {
            const res = await fetch('/login', {
                method : "POST",
                headers : {
                    "Content-Type" : "application/json"
                },
                body : JSON.stringify({
                    email,password
                })
            });
            if(res.status == 400 || !res){
                window.alert("Invalid Credential")
            }else{
                window.alert("Login Successfully");
                window.location.reload();
            }
        }
        catch (error){
            console.log(error);
        }
    }

    return (
        <div>
            <div className="container shadow my-5">
                <div className="row">
                    <div className="col-md-5 d-flex flex-column align-items-center text-white justify-content-center form">
                        <h1 className="display-4 fw-bolder">Welcome Back</h1>
                        <p className="lead">Enter Your Credentials To Login</p>
                        <h5 className='mb-4'>OR</h5>
                        <NavLink to="/register" className="btn btn-outline-light rounded-pill pb-2 w-50">Register</NavLink>
                    </div>
                    <div className="col-md-6 m-2 py-5">
                        <h1 className="display-6 fw-bolder mb-5">LOGIN</h1>
                        <form onSubmit={handleSubmit}>
                            <div class="mb-3">
                                <label for="exampleInputEmail1" class="form-label">Email address</label>
                                <input type="email" name='email' value={user.email} onChange={handleChange} class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                                    <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
                            </div>
                            <div class="mb-3">
                                <label for="exampleInputPassword1" class="form-label">Password</label>
                                <input type="password" name='password' value={user.password} onChange={handleChange} class="form-control" id="exampleInputPassword1"/>
                                <div id="emailHelp" class="form-text">We'll never share your also password with anyone else.</div>
                            </div>
                            <div class="mb-3 form-check">
    <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
    <label class="form-check-label" for="exampleCheck1">Remember me</label>
  </div>
                            <button type="submit" class="btn btn-primary w-100 mt-4 rounded-pill">Login</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}
